import React, { Profiler, useState } from 'react';

// + or - icons
 import { AiOutlineMinus, AiOutlinePlus } from 'react-icons/ai';


 const Question = ({id , title , info}) => {

   const [showInfo ,setshowInfo]=useState(false);
   const infoHandler= ()=>{
    setshowInfo(prev =>!prev)

   }
   const checkShoInfo= (id , phase , startTime , commitTime)=>{
    console.log( 'id  :' ,id ,'phase  :', phase ,'startTime  :', startTime ,'  :', commitTime);

   }
  return (
    <article className='question'  onClick={infoHandler}>
      <header>
         <h4>{title}</h4> 
        <button className='btn'>
          {showInfo ? <AiOutlineMinus /> : <AiOutlinePlus />} 
        </button>
      </header>
      <Profiler id='check-shoInfo' onRender={checkShoInfo}>

      {showInfo && <p>{info}</p>} 
      </Profiler>
    </article>
  );
};

export default Question;
