import React, { useState } from "react";
import menu from "../data";

const Categories = () => {
  const [menuList , setMenuList]=useState(menu)
  const [categorySelect , setCategorySelect]=useState()

  const categoryArray= new Set() 
  menuList.map(cat =>(
      categoryArray.add(cat.category)
      ))
    const  categoryList=[...categoryArray]


  console.log("categoryArray" ,categoryList);

  return (
    <div className="btn-container">
      <button type="button"// highlight class  for highlight main category
      className="filter-btn"
      >
        All
      </button>
        {categoryList.forEach(category =>{

      <button type="button"// highlight class  for highlight main category
      className="filter-btn" key={category.id}
      >
       {category}
       {console.log(category)}
      </button>


        })}  

    </div>
  );
};

export default Categories;
