import React, { useState } from "react";
import menu from "../data";

const Categories = () => {
  const [menuList , setMenuList]=useState(menu)
  const [categorySelect , setCategorySelect]=useState()

  const categoryArray= new Set() 
  menuList.map(cat =>(
      categoryArray.add(cat.category)
      ))
    const  categoryList=['All',...categoryArray]

    const selectcategoryFunc=(category)=>{
      setCategorySelect(category)

    }

  return (
    <div className="btn-container">
              {categoryList.map(category =>(

      <button type="button" 
      className={category ? "filter-btn " : "filter-btn"}

      key={category}
      onClick={()=>selectcategoryFunc(category)}
      >
       {category}
      </button>


       ))}  

    </div>
  );
};

export default Categories;
