   const [showInfo ,setshowInfo]=useState(false);
   const infoHandler= ()=>{
    setshowInfo(prev =>!prev)

   }
  return (
    <article className='question'>
      <header>
         <h4>{title}</h4> 
        <button className='btn'>
          {showInfo ? <AiOutlineMinus /> : <AiOutlinePlus />} 
        </button>
      </header>
      {showInfo && <p>{info}</p>} 
    </article>
  );
};

export default Question;
