import React, { useState } from "react";
import menu from "../data";
import Menu from './Menu';

const Categories = () => {
  const [menuList, setMenuList] = useState(menu);
  const [categorySelect, setCategorySelect] = useState("All");

  const categoryArray =[...new Set( menuList.map(cat => cat.category))];
  //برای از بین بردن 

  const categoryList = ["All", ...categoryArray];

  const selectcategoryFunc = (category) => {
    setCategorySelect(category);
  };

  return (
    <>
    <div className="btn-container">
      {categoryList.map((category) => (
        <>
        <button
        type="button"
        className={
          category === categorySelect ? "filter-btn highlight" : "filter-btn"
        }
        key={category}
        onClick={() => selectcategoryFunc(category)}
        >
          {category}
        </button>

          </>
      ))}
    </div>
      </>
  );
};

export default Categories;
